import sql from "mssql";

const dbSettings = {

    server : "localhost",
    database : "Securos",
    user : "sa",
    password : "SIAserver1234",    
    port : 61959,
    options:{
        encrypt: true,
        trustServerCertificate: true,
    },

};

export async function getConnection(){
   try{
    const pool = await sql.connect(dbSettings);
    return pool; 
   }catch (error){
    console.error(error)
   }
}

export {sql};

