import {getConnection, sql, queries} from '../database'


export const getProducts = async  (req, res) => {
try {
    const pool = await getConnection();

const result = await pool.request().query(queries.getAllProduct)
console.log(result)
res.json(result.recordset)
} catch (error) {
    res.status(500);
    res.send(error.message);
}
};

export const createNewProduct = async (req, res) => {
const {name,password} = req.body
let { id } = req.body;

if(name == null || password == null || id == null){
    return res.status(400).json({msg: "BAD REQUEST"})
}

if (id == null) id = 0;
try{
    const pool = await getConnection();

    await pool.request()
    .input("name", sql.Text,name)
    .input("password", sql.Text,password)
    .input("id", sql.Int,id)
    .query(
        queries.createProduct
        )
    
    res.json({name,password,id})
} catch(error) {
    res.status(500);
    res.send(error.message);
}
}

export const getProductById = async (req,res) => {  
    const{id} = req.params;
    const pool = await getConnection()
    const result = await pool.request().input("Id", id).query(queries.getProductById)
  
 res.send(result.recordset[0])
}

export const deleteProduct = async (req,res) => {  
    const{id} = req.params;
    const pool = await getConnection()
    const result = await pool.request().input("Id", id).query(queries.deleteProduct)
  
 res.send(result)
}

export const getTotalProducts = async (req,res) => {  
    
    const pool = await getConnection()
    const result = await pool
    .request()
    .query(queries.getTotalProducts)
  
   res.json(result.recordset[0][''])   
}

